import React from 'react'
import RecipientInfo   from './RecipientInfo'
import InsuredInfo   from './InsuredInfo'
import { connect } from 'react-redux'
import { saveRecipientInfo } from '../../actions/LoginAndSignUpActions.js'

class MainForm extends React.Component {
  state = {
    step:1,
    recipient:{},
    claim:{},
  }
  // state = {
  //   recipient:{first_name:"dfg",},
  //   claim:{},
  //   insurance:{}
  // }

   formData = [
      {label:"Patient's last name",name:"2_lname",category:"recipient"},
      {label:"Patient's first name",name:"2_fname",category:"recipient"},
      {label:"13. Claim ICD",name:"icd_code",category:"claim"},
      {label:"14. Procedure label",name:"procedure_code",category:"claim"},
      {label:"1.Choose Insurance",name:"insurance_name",category:"insurance"}
  ]

  nextStep = () => {
    const { step } = this.state
    this.setState({
      step: step + 1
    })
  }

  prevStep = () => {
    const { step } = this.state
    this.setState({
      step: step - 1
    })
  }


  handleChange = (e) => {
    console.log(e.target.dataset.category,e.target.name,e.target.value);
    let categoryName = e.target.dataset.category
    let newState = {...this.state, [categoryName]: {...this.state[categoryName], [e.target.name]:e.target.value}}
    this.setState({...newState})
    console.log(this.state);
  }

  handleSubmit = (e) => {
    e.preventDefault()
    console.log(this.state);
    // this.props.saveRecipientInfo(this.state)
  }

  render() {
    const { step } = this.state

    switch (step) {
      case 1:
        return (
          < RecipientInfo handleChange={this.handleChange} nextStep={this.nextStep} prevStep={this.prevStep} state={this.state} formData={this.formData}/>
        )
      case 2:
        return (
            < InsuredInfo handleChange={this.handleChange} nextStep={this.nextStep} prevStep={this.prevStep} state={this.state} formData={this.formData}/>
        )

      default:

    }
  }
}

  // const mapStateToProps = (state) => {
  //   return {
  //     user: state.user
  //   }
  // }

  const mapDispatchToProps = (dispatch) => {
    return {
      saveRecipientInfo: (data) => dispatch(saveRecipientInfo(data))
    }
  }

  export default connect(null, mapDispatchToProps)(MainForm)
