import React from 'react'
import TextField from 'material-ui/TextField';
import PropTypes from "prop-types";
import { withStyles } from '@material-ui/core/styles';
import DatePicker from 'material-ui/DatePicker'
import { usStates, getFormInputData } from '../../dataLists.js'
// import RaisedButton from 'material-ui/RaisedButton';
// import { Button, Form, Grid, Header, Image, Message, Segment, Input, Label } from 'semantic-ui-react'

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },
});


class RecipientInfo extends React.Component {
  state = {

  }

  handleChange = (e) => {
    console.log(e.target,e.target.name,e.target.value);
    let categoryName = e.target.dataset.category
    let newState = {...this.state, [categoryName]: {...this.state[categoryName], [e.target.name]:e.target.value}}
    this.setState({...newState})
    console.log(this.state)
  }

  handleChangeDate = (e,date) => {
    console.log(date)
    let newState = {...this.state, ["p_dob"]:date, recipient : {...this.state.recipient, ["3_mm"]:date.getMonth(),["3_dd"]:date.getDate(),["3_yy"]:date.getFullYear()} }
    this.setState({...newState})
      console.log(this.state);
  }

  render(){
    const { classes } = this.props
    debugger
    const part1Inputs = getFormInputData().part1

    // const name1 = part1Inputs.name
    console.log("inside Rinfo render")
    return (
        <React.Fragment>
          <TextField
            hintText="Please type..."
            floatingLabelText={part1Inputs.label}
            onChange={this.handleChange}
            data-category={part1Inputs.category}
            defaultValue={this.state.name1}
            name={name1}
            style={{width:700}}
            margin="normal"
          />
          < br />



          < br />
          <DatePicker
            hintText="Patient's Date of Birth"
            value={this.state.p_dob}
            onChange={this.handleChangeDate}
            />
        </React.Fragment>

    )
   }
  }
  RecipientInfo.propTypes = {
    classes: PropTypes.object.isRequired,
  };

export default withStyles(styles)(RecipientInfo)
