import React from 'react'
import SignUp   from './SignUp'
import { BrowserRouter as Router, Route, Link } from "react-router-dom"

class Login extends React.Component {
  state = {
    email:"",
    password:""
  }

  // componentDidMount() {
  //   this.handleFetchRegister()
  // }



  handleFetchLogin = () => {

    fetch(('http://localhost:3000/api/v1/auth/login'), {
      method: 'POST',
      headers: {
          "Content-Type": "application/json"
      },
      body: JSON.stringify(this.state),
    }).then(res => res.json()).then(console.log)
  }



  handlerSubmitLogin =  (e) => {
    e.preventDefault()
    this.handleFetchLogin()
  }

  handlerChange = (e) => {
    this.setState({
      [e.target.name]:e.target.value
    })
  }
  handleSignUp =  (e) => {
    return <Link to="/signup">Topics</Link>

  }

  render() {
    return (
      <Router>
        <React.Fragment>
      <div>
        <br></br>
      <h2>Log in</h2>
        <form onSubmit={this.handlerSubmitLogin}>

          <label>

            Email:
            <input type="text" name="email" value={this.state.email} onChange={this.handlerChange}/>
            Password:
            <input type="text" name="password" value={this.state.password} onChange={this.handlerChange}/>
          </label>
          <input type="submit" value="Login" />
        </form>

        <Link to="/signup">Sign up</Link>
      </div>
      <Route path="/signup" component={SignUp} />
    </React.Fragment>
    </Router>

    )
    }
  }

export default Login
