import React from 'react'
// import testChildComponent   from './testChildComponent'

class Login extends React.Component {
  state = {
    name:"",
    email:"",
    password:""
  }

  // componentDidMount() {
  //   this.handleFetchRegister()
  // }

  handleFetchRegister = () => {
    fetch(('http://localhost:3000/api/v1/auth/register'), {
      method: 'POST',
      headers: {
          "Content-Type": "application/json"
      },
      body: JSON.stringify(this.state),
    }).then(res => res.json()).then(console.log)
  }

  handleFetchLogin = () => {
    let login = {...this.state}
    delete login.name
    fetch(('http://localhost:3000/api/v1/auth/login'), {
      method: 'POST',
      headers: {
          "Content-Type": "application/json"
      },
      body: JSON.stringify(login),
    }).then(res => res.json()).then(console.log)
  }

  handlerSubmit =  (e) => {
    e.preventDefault()
    this.handleFetchRegister()
  }

  handlerSubmitLogin =  (e) => {
    e.preventDefault()
    this.handleFetchLogin()
  }

  handlerChange = (e) => {
    this.setState({
      [e.target.name]:e.target.value
    })
  }

  render() {
    return (
      <div>
        <h1>Sign up</h1>
        <form onSubmit={this.handlerSubmit}>
          <label>
            Name:
            <input type="text" name="name" value={this.state.name} onChange={this.handlerChange}/>
            Email:
            <input type="text" name="email" value={this.state.email} onChange={this.handlerChange}/>
            Password:
            <input type="text" name="password" value={this.state.password} onChange={this.handlerChange}/>
          </label>
          <input type="submit" value="Submit" />
        </form><br></br>
      <h2>Log in</h2>
        <form onSubmit={this.handlerSubmitLogin}>

          <label>

            Email:
            <input type="text" name="email" value={this.state.email} onChange={this.handlerChange}/>
            Password:
            <input type="text" name="password" value={this.state.password} onChange={this.handlerChange}/>
          </label>
          <input type="submit" value="Submit" />
        </form>
      </div>
    )
    }
  }

export default Login
