import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import TextField from 'material-ui/TextField';
import RecipientInfo   from './RecipientInfo'
// import InsuredInfo   from './InsuredInfo'
import { connect } from 'react-redux'
import { saveRecipientInfo } from '../../actions/LoginAndSignUpActions.js'
import RaisedButton from 'material-ui/RaisedButton';


const styles = theme => ({
  root: {
    width: "90%"
  },
  backButton: {
    marginRight: theme.spacing.unit
  },
  instructions: {
    marginTop: theme.spacing.unit,
    marginBottom: theme.spacing.unit
  }
});



function getSteps() {
  return [
    "Recipient Info",
    "Page2",
    "Page3",
    "Page4",
    "Page5",

  ];
}
 function getFormInputData (){
  return (
    {
       "page1": [
         {label:"Patient's last name",name:"2_lname",category:"recipient", inputType:"text"},
         {label:"Patient's first name",name:"2_fname",category:"recipient" },
         {label:"Patient's middle initial",name:"2_mi",category:"recipient" },
         {label:"Patient's street address",name:"5_street",category:"recipient" },
         {label:"City",name:"5_city",category:"recipient" }
       ],
       "page2": [
         {label:"Patient's last name",name:"2_lname",category:"recipient", inputType:"text"}
       ]
   }
  )
}



class MainForm1 extends React.Component {
  state = {
    activeStep: 0,
    recipient:{
      // "2_lname":"",
      // "2_fname":"",
      // "2_mi":"",
      // "5_street":"",
      // "5_city":""
    },
    claim:{}
  }

  handleChangeDate = (e,date) => {
      console.log(date.getMonth(),date.getFullYear(),date.getDate())

  }
  handleChange = (e,date) => {
    if (e){
      console.log(e.target.name,e.target.value);
      let categoryName = e.target.dataset.category
      let newState = {...this.state, [categoryName]: {...this.state[categoryName], [e.target.name]:e.target.value}}
      this.setState({...newState})
      console.log(this.state);
    }
  }

   getStepContent=(stepIndex)=> {
    switch (stepIndex) {
      case 0:
        return (
          < RecipientInfo page1={getFormInputData().page1} mainState={this.state.recipient} handleChange={this.handleChange}/>
        )
      case 1:
        return (
          < TextField />
        )
      case 2:
        return "This is the bit I really care about!";
      default:
        return "Uknown stepIndex";
    }
  }


  handleNext = () => {
    this.setState(state => ({
      activeStep: state.activeStep + 1
    }));

  };

  handleBack = () => {
    this.setState(state => ({
      activeStep: state.activeStep - 1
    }));
  };

  handleReset = () => {
    this.setState({
      activeStep: 0
    });
  };

  render() {
    const { classes } = this.props;
    const steps = getSteps();
    const { activeStep } = this.state;

    return (
      <div className={classes.root}>
        <Stepper activeStep={activeStep} alternativeLabel>
          {steps.map(label => {
            return (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            );
          })}
        </Stepper>
        <div>
          {this.state.activeStep === steps.length ? (
            <div>
              <Typography className={classes.instructions}>
                All steps completed
              </Typography>
              <Button onClick={this.handleReset}>Reset</Button>
            </div>
          ) : (
            <div>
              <Typography className={classes.instructions}>
                {this.getStepContent(activeStep)}
              </Typography>
              <div>
              <RaisedButton
                disabled={activeStep === 0}
                label="Back"
                primary={false}
                style={stylesButton.button}
                onClick={this.handleBack}
                className={classes.backButton}
              />
              <RaisedButton

                label={activeStep === steps.length - 1 ? "Finish" : "Next"}
                primary={true}
                style={stylesButton.button}
                onClick={this.handleNext}
                className={classes.backButton}
              />



              </div>
            </div>
          )}
        </div>
      </div>
    );
  }
}
const stylesButton = {
  button: {
    margin: 15
  }
};


MainForm1.propTypes = {
  classes: PropTypes.object
};




export default withStyles(styles)(MainForm1);
