import React from 'react'
import LoginForm from '../components/LoginForm.js'
import SignUp   from './SignUp'
import { BrowserRouter as Router, Route, Link } from "react-router-dom"
import { connect } from 'react-redux'
import {loginUser} from '../actions/LoginAndSignUpActions'

class Login extends React.Component {
  state = {
    email:"",
    password:""
  }

  // componentDidMount() {
  //   this.handleFetchRegister()
  // }

  componentDidMount() {
    const token = localStorage.getItem("token")
    this.props.authorize_token(token)
  }

  handlerSubmitLogin =  (e) => {
    e.preventDefault()
    this.props.loginUser(this.state)
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]:e.target.value
    })
  }

  mainPage = () => {
   if (this.props.user.id != null) {
     return (<HomePage />)
   } else {
     return (<LoginForm handleLoginSubmit={this.handleLoginSubmit} handleLoginFormChange={this.handleLoginFormChange} />)
   }
 }

  render() {
    console.log(this.props.token);
    return (

      <div>
      <h2>Log in</h2>
        <form onSubmit={this.handlerSubmitLogin}>
          <label>
            Email:
            <input type="text" name="email" value={this.state.email} onChange={this.handleChange}/>
            Password:
            <input type="text" name="password" value={this.state.password} onChange={this.handleChange}/>
          </label>
          <input type="submit" value="Login" />
        </form>
        <Link to="/signup">Sign up</Link>
      </div>

    )
    }
  }

  const mapStateToProps = (state) => {
    return { token: state.token}
  }

  const mapDispatchToProps = (dispatch) => {
    return {
      loginUser: (credentials) => dispatch(loginUser(credentials)),
      authorize_token:(arg) => dispatch(authorizeToken(arg))
    }
  }

export default connect(mapStateToProps,mapDispatchToProps)(Login)
