import React from 'react'
import InputTag from './InputTag'
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import TextField from 'material-ui/TextField';
import RaisedButton from 'material-ui/RaisedButton';
// import { Button, Form, Grid, Header, Image, Message, Segment, Input, Label } from 'semantic-ui-react'

const RecipientInfo = ({ handleChange, handleSubmit, state, formData }) => {
   const inputCollection = formData.map(dataObj => < InputTag key={dataObj.name} dataObj={dataObj} handleChange={handleChange} state={state}/>)
    return (



        <div className='recipient-form'>

      <style>{`
        body > div,
        body > div > div,
        body > div > div > div.recipient-form {
          height: 100%;
        }
      `}</style>

      <Grid textAlign='center' style={{ height: '100%' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 600 }}>
          <Header as='h2' color='teal' textAlign='center'>
             CM 1500 RecipientInfo
          </Header>
          <Form size='large'>
            <Segment stacked>
              {inputCollection}
              <Button color='teal' fluid size='large'>
                Back
              </Button>
              <Button color='teal' fluid size='large'>
                Continue
              </Button>
            </Segment>
          </Form>

        </Grid.Column>
      </Grid>
    </div>




    )

  }

export default RecipientInfo
