export const loginUser = (credentials) => {
  return (dispatch) => {
    fetch(('http://localhost:3000/api/v1/auth/login'), {
      method: 'POST',
      headers: {
          "Content-Type": "application/json"
      },
      body: JSON.stringify(credentials),
    }).then(res => res.json())
  .then(respond => dispatch({type: 'LOGIN', payload: respond}))
}
}

