import React from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import TextField from 'material-ui/TextField';
import RecipientInfo   from './RecipientInfo'
import RecipientsAddress   from './RecipientsAddress'
import { usStates, getFormInputData } from '../../dataLists'
// import InsuredInfo   from './InsuredInfo'
import { connect } from 'react-redux'
import { saveRecipientInfo } from '../../actions/LoginAndSignUpActions.js'
import RaisedButton from 'material-ui/RaisedButton';

const page1 = getFormInputData().page1
const styles = theme => ({
  root: {
    width: "90%"
  },
  backButton: {
    marginRight: theme.spacing.unit
  },
  instructions: {
    marginTop: theme.spacing.unit,
    marginBottom: theme.spacing.unit
  }
});



function getSteps() {
  return [
    "Patient's Info",
    "Patient's Address",
    "Page3",
    "Page4",
    "Page5",

  ];
}




class MainForm1 extends React.Component {
  state = {
    activeStep: 0,
    recipient:{},
    claim:{}
  }

  handleChangeDate = (e,date) => {
    console.log(date)
    let newState = {...this.state, ["p_dob"]:date, recipient : {...this.state.recipient, ["3_mm"]:date.getMonth(),["3_dd"]:date.getDate(),["3_yy"]:date.getFullYear()} }
    this.setState({...newState})
      console.log(this.state);
  }
  handleChangeDropDown = (e) => {
    let newState = {...this.state, recipient : {...this.state.recipient, [e.target.name]:e.target.value}}
    this.setState({...newState},() => {
      console.log(this.state);
    })
  }

  handleChange = (e) => {

    if (e){
      console.log(e.target,e.target.name,e.target.value);
      let categoryName = e.target.dataset.category
      let newState = {...this.state, [categoryName]: {...this.state[categoryName], [e.target.name]:e.target.value}}
      this.setState({...newState})
      console.log(this.state);
    }

  }

  getformatedDate = (yyyy, mm, dd) => {
    console.log(this.state.recipient.yyyy);

    // const yyyy = this.state.recipient[page1[3][yyyy]]
    // const mm = this.state.recipient[page1[3][mm]]
    // const dd = this.state.recipient[page1[3][dd]]
    // let fd;
    // if (yyyy && mm && dd){
    //   fd = page1[3].formatDate(yyyy,mm,dd)
    //
    // }
    //
    // return fd
  }

   getStepContent=(stepIndex)=> {
    switch (stepIndex) {
      case 0:
        return (
          < RecipientInfo page1={page1} mainState={this.state.recipient} handleChange={this.handleChange}
          handleChangeDate={this.handleChangeDate} getformatedDate={this.getformatedDate}
          />

        )
      case 1:
        return (
          < RecipientsAddress page2={getFormInputData().page2} mainState={this.state.recipient}
          handleChange={this.handleChange}
          handleChangeDropDown={this.handleChangeDropDown
          }
          />
        )
      case 2:
        return "This is the bit I really care about!";
      default:
        return "Uknown stepIndex";
    }
  }


  handleNext = () => {
    this.setState(state => ({
      activeStep: state.activeStep + 1
    }));

  };

  handleBack = () => {
    this.setState(state => ({
      activeStep: state.activeStep - 1
    }));
  };

  handleReset = () => {
    this.setState({
      activeStep: 0
    });
  };

  render() {
    const { classes } = this.props;
    const steps = getSteps();
    const { activeStep } = this.state;
    console.log("inside Main render", this.state)
    return (
      <div className={classes.root}>
        <Stepper activeStep={activeStep} alternativeLabel>
          {steps.map(label => {
            return (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            );
          })}
        </Stepper>
        <div>
          {this.state.activeStep === steps.length ? (
            <div>
              <Typography className={classes.instructions}>
                All steps completed
              </Typography>
              <Button onClick={this.handleReset}>Reset</Button>
            </div>
          ) : (
            <div>
              <Typography className={classes.instructions}>
                {this.getStepContent(activeStep)}
              </Typography>
              <div>
              <RaisedButton
                disabled={activeStep === 0}
                label="Back"
                primary={false}
                style={stylesButton.button}
                onClick={this.handleBack}
                className={classes.backButton}
              />
              <RaisedButton

                label={activeStep === steps.length - 1 ? "Finish" : "Next"}
                primary={true}
                style={stylesButton.button}
                onClick={this.handleNext}
                className={classes.backButton}
              />



              </div>
            </div>
          )}
        </div>
      </div>
    );
  }
}
const stylesButton = {
  button: {
    margin: 15
  }
};


MainForm1.propTypes = {
  classes: PropTypes.object
};




export default withStyles(styles)(MainForm1);
