const initialState =
{ token:"",
  login:{
        email:"",
        password:""
        },
  signup:{
        name:null,
        email:null,
        password:null
        }
}

export default function reducer(state = initialState, action) {
  let newState = {...state}
  switch (action.type) {
    case "LOGIN":
       newState.token = action.payload.access_token
       localStorage.setItem("token",newState.token)
      return newState
    default:
      return newState
  }
}
