import React from 'react'
import RecipientInfo   from './RecipientInfo'
import { connect } from 'react-redux'
import { saveRecipientInfo } from '../../actions/LoginAndSignUpActions.js'

class MainForm extends React.Component {
  state = {
    recipient:{},
    claim:{},
    insurance:{}
  }
  form = [
      {recipient:[{label:"2.Patient first name",name:"first_name"},{label:"2a.Patient last name",name:"last_name"}]},
      {claim:[{label:"13. Claim ICD",name:"icd_code"},{label:"14. Procedure label",name:""}]},
      {insurance:[{label:"1.Choose Insurance",name:"insurance_name"},{label:"Patient last name",name:"last_name"}]}
  ]

  handleChange = (e) => {
    // let inputValue = {[e.target.dataset.category] : {[e.target.name]:e.target.value}}

    // console.log(inputValue)
    this.setState({
      [e.target.dataset.category] : {[e.target.name]:e.target.value}
    })
  }

  handleSubmit = (e) => {
    e.preventDefault()
    console.log(this.state);
    this.props.saveRecipientInfo(this.state)
  }

  render() {
    return (
      <div>
      < RecipientInfo handleChange={this.handleChange} handleSubmit={this.handleSubmit} state={this.state} form={this.form}/>
      </div>)
    }
  }
  // const mapStateToProps = (state) => {
  //   return {
  //     user: state.user
  //   }
  // }

  const mapDispatchToProps = (dispatch) => {
    return {
      saveRecipientInfo: (data) => dispatch(saveRecipientInfo(data))
    }
  }

  export default connect(null, mapDispatchToProps)(MainForm)
