import React from 'react'
import InputTag from './InputTag'
import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import TextField from 'material-ui/TextField';
import RaisedButton from 'material-ui/RaisedButton';
// import { Button, Form, Grid, Header, Image, Message, Segment, Input, Label } from 'semantic-ui-react'


class RecipientInfo extends React.Component {

   continue = (e) => {
      e.preventDefault()
      this.props.nextStep()
   }

   back = (e) => {
     e.preventDefault()
     this.props.prevStep()
   }

  render(){
    const { handleChange, state, formData, nextStep, prevStep } = this.props

    const inputCollection = formData.page1.map(dataObj => < InputTag key={dataObj.name} dataObj={dataObj} handleChange={handleChange} state={state} />)

    return (

        <React.Fragment>

          {inputCollection}
          <RaisedButton
            label="Continue"
            primary={true}
            style={styles.button}
            onClick={this.continue}
          />
        </React.Fragment>

    )
   }
  }

  const styles = {
    button: {
      margin: 15
    }
  }

export default RecipientInfo
