import React from 'react'
import TextField from 'material-ui/TextField';
import PropTypes from "prop-types";
import { withStyles } from '@material-ui/core/styles';
import DatePicker from 'material-ui/DatePicker'
import { getFormInputData } from '../../dataLists.js'
// import RaisedButton from 'material-ui/RaisedButton';
// import { Button, Form, Grid, Header, Image, Message, Segment, Input, Label } from 'semantic-ui-react'

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
    width: 200,
  },
});


class RecipientInfo extends React.Component {

  handleChange = (e) => {
    // console.log(e.target.category,e.target.name,e.target.value);
    let categoryName = e.target.dataset.category
    let newState = {...this.state, [categoryName]: {...this.state[categoryName], [e.target.name]:e.target.value}}
    this.setState({...newState})
    console.log(this.state)
  }

  handleChangeDate = (e,date) => {
    console.log(date)
    let newState = {...this.state, ["p_dob"]:date, recipient : {...this.state.recipient, ["3_mm"]:date.getMonth(),["3_dd"]:date.getDate(),["3_yy"]:date.getFullYear()} }
    this.setState({...newState})
  }

  render(){
    const { classes, page1, mainState } = this.props
    const name0 = page1[0].name
    const name1 = page1[1].name
    const name2 = page1[2].name
    const name3 = page1[3].name
    console.log("inside Rinfo render",mainState)
    return (
        <React.Fragment>
          <TextField
            hintText="Please type..."
            floatingLabelText={page1[0].label}
            onChange={this.handleChange}
            data-category={page1[0].category}
            defaultValue={mainState.name0}
            name={name0}
            style={{width:700}}
            margin="normal"
          />
          < br />
          <TextField
            hintText="Please type..."
            floatingLabelText={page1[1].label}
            onChange={this.handleChange}
            data-category={page1[1].category}
            defaultValue={mainState.name1}
            name={name1}
            style={{width:700}}
            margin="normal"
          />
          < br />
          <TextField
            hintText="Please type..."
            floatingLabelText={page1[2].label}
            onChange={this.handleChange}
            data-category={page1[2].category}
            defaultValue={mainState.name2}
            name={name2}
            style={{width:700}}
            margin="normal"
          />
          < br />


          < br />
          <DatePicker
            hintText={page1[3].label}
            value={mainState.p_dob}
            onChange={this.handleChangeDate}
            />
        </React.Fragment>

    )
   }
  }
  RecipientInfo.propTypes = {
    classes: PropTypes.object.isRequired,
  };

export default withStyles(styles)(RecipientInfo)
