import React from 'react'
import RecipientInfo   from './RecipientInfo'
import { connect } from 'react-redux'
import { saveRecipientInfo } from '../../actions/LoginAndSignUpActions.js'

class MainForm extends React.Component {
  state = {
    step:1,
    recipient:{},
    claim:{},
  }
  // state = {
  //   recipient:{first_name:"dfg",},
  //   claim:{},
  //   insurance:{}
  // }

   formData = [
      {label:"2.Patient first name",name:"1_a",category:"recipient"},
      {label:"2a.Patient last name",name:"1_mm",category:"recipient"},
      {label:"13. Claim ICD",name:"icd_code",category:"claim"},
      {label:"14. Procedure label",name:"procedure_code",category:"claim"},
      {label:"1.Choose Insurance",name:"insurance_name",category:"insurance"}
  ]

  nextStep = () => {
    const { step } = this.state
    this.setState({
      step: step + 1
    })
  }

  preStep = () => {
    const { step } = this.state
    this.setState({
      step: step - 1
    })
  }


  handleChange = (e) => {
    let categoryName = e.target.dataset.category
    let newState = {...this.state, [categoryName]: {...this.state[categoryName], [e.target.name]:e.target.value}}
    this.setState({...newState})
    console.log(this.state);
  }

  handleSubmit = (e) => {
    e.preventDefault()
    console.log(this.state);
    // this.props.saveRecipientInfo(this.state)
  }

  render() {
    const { step } = this.state

    switch (step) {
      case 1:
        return (
          < RecipientInfo handleChange={this.handleChange} nextStep={nextStep} handleSubmit={this.handleSubmit} state={this.state} formData={this.formData}/>
        )
      case 2:
        return (
          <h1>Another Form</h1>
        )

      default:

    }


    return (
      <div>
      < RecipientInfo handleChange={this.handleChange} handleSubmit={this.handleSubmit} state={this.state} formData={this.formData}/>
      </div>)
    }
  }
  // const mapStateToProps = (state) => {
  //   return {
  //     user: state.user
  //   }
  // }

  const mapDispatchToProps = (dispatch) => {
    return {
      saveRecipientInfo: (data) => dispatch(saveRecipientInfo(data))
    }
  }

  export default connect(null, mapDispatchToProps)(MainForm)
